INSERT INTO `wp_posts` VALUES("1", "1", "2014-01-28 19:55:27", "2014-01-28 19:55:27", "Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!", "Hello world!", "", "publish", "open", "open", "", "hello-world", "", "", "2014-01-28 19:55:27", "2014-01-28 19:55:27", "", "0", "http://americadecoded.localhost/?p=1", "0", "post", "", "1");
INSERT INTO `wp_posts` VALUES("2", "1", "2014-01-28 19:55:27", "2014-01-28 19:55:27", "This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://americadecoded.localhost/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!", "Sample Page", "", "trash", "open", "open", "", "sample-page", "", "", "2014-02-01 18:17:03", "2014-02-01 18:17:03", "", "0", "http://americadecoded.localhost/?page_id=2", "0", "page", "", "0");
INSERT INTO `wp_posts` VALUES("3", "1", "2014-01-28 21:28:55", "0000-00-00 00:00:00", "", "Auto Draft", "", "auto-draft", "open", "open", "", "", "", "", "2014-01-28 21:28:55", "0000-00-00 00:00:00", "", "0", "http://americadecoded.localhost/?p=3", "0", "post", "", "0");
INSERT INTO `wp_posts` VALUES("4", "1", "2014-02-01 18:17:03", "2014-02-01 18:17:03", "This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://americadecoded.localhost/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!", "Sample Page", "", "inherit", "open", "open", "", "2-revision-v1", "", "", "2014-02-01 18:17:03", "2014-02-01 18:17:03", "", "2", "http://americadecoded.localhost/?p=4", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("5", "1", "2014-02-01 18:19:53", "2014-02-01 18:19:53", "<header class=\"col-sm-12\">\r\n    <h1>Accessible Law for the Internet Age</h1>\r\n    <p>America’s laws belong to the people. Nothing should stand between you and accessing the law.</p>\r\n</header>\r\n\r\n<div class=\"col-sm-4 index3\">\r\n    <article class=\"feature\">\r\n	<div class=\"img-container\"><img src=\"http://americadecoded.localhost/wp-content/uploads/2014/02/index-icons-01.png\" alt=\"index-icons-01\" width=\"112\" height=\"112\" class=\"alignnone size-full wp-image-11\" /></div>\r\n\r\n	On-demand federal, state and local laws--all in one place.\r\n    </article>\r\n</div><!--end column 1-->\r\n<div class=\"col-sm-4 index3\">\r\n    <article class=\"feature\">\r\n	<div class=\"img-container\"><img src=\"http://americadecoded.localhost/wp-content/uploads/2014/02/index-icons-02.png\" alt=\"index-icons-02\" width=\"112\" height=\"112\" class=\"alignnone size-full wp-image-12\" /></div>\r\n\r\n	All the powerful tools lawyers use, at your fingertips.\r\n    </article>\r\n</div><!--end column 2-->\r\n<div class=\"col-sm-4 index3\">\r\n    <article class=\"feature\">\r\n	<div class=\"img-container\"><img src=\"http://americadecoded.localhost/wp-content/uploads/2014/02/index-icons-03.png\" alt=\"index-icons-03\" width=\"112\" height=\"112\" class=\"alignnone size-full wp-image-13\" /></div>\r\n\r\n	Finally: federal, state, and local laws that are easy to access, use, and understand.\r\n    </article>\r\n</div><!--end column 3-->", "Home", "", "publish", "closed", "closed", "", "home", "", "", "2014-02-01 19:33:09", "2014-02-01 19:33:09", "", "0", "http://americadecoded.localhost/?page_id=5", "0", "page", "", "0");
