INSERT INTO `wp_posts` VALUES("22", "1", "2014-02-01 22:00:06", "2014-02-01 22:00:06", "<h2>America\'s Laws Are the People\'s Public Property</h2>\r\n<p>The State Decoded software provides you with a people-friendly way to access your local, state, and federal legal code. </p>\r\n\r\n<ul class=\"about-list\">\r\n	<li><img src=\"http://americadecoded.localhost/wp-content/uploads/2014/02/about-icons-01.png\" alt=\"about-icons-01\" width=\"52\" height=\"52\" class=\"alignnone size-full wp-image-23\" />Careful organization by article and section makes browsing a breeze.</li>\r\n	<li><img src=\"http://americadecoded.localhost/wp-content/uploads/2014/02/about-icons-02.png\" alt=\"about-icons-02\" width=\"52\" height=\"52\" class=\"alignnone size-medium wp-image-24\" />A site-wide search allows you to find the laws you’re looking for by topic.</li>\r\n	<li><img src=\"http://americadecoded.localhost/wp-content/uploads/2014/02/about-icons-03.png\" alt=\"about-icons-03\" width=\"52\" height=\"52\" class=\"alignnone size-medium wp-image-25\" />Scroll-over definitions translate legal jargon into common English.</li>\r\n	<li><img src=\"http://americadecoded.localhost/wp-content/uploads/2014/02/about-icons-04.png\" alt=\"about-icons-04\" width=\"52\" height=\"52\" class=\"alignnone size-medium wp-image-26\" />Downloadable developer-friendly legal code lets you take the law into your own hands.</li>\r\n	 \r\n	<li><img src=\"http://americadecoded.localhost/wp-content/uploads/2014/02/about-icons-05.png\" alt=\"about-icons-05\" width=\"53\" height=\"52\" class=\"alignnone size-full wp-image-27\" />Best of all, everything on the site remains cost-and restriction-free.</li>\r\n</ul>", "About Us", "", "publish", "closed", "closed", "", "about", "", "", "2014-02-01 22:22:10", "2014-02-01 22:22:10", "", "0", "http://americadecoded.localhost/?page_id=22", "0", "page", "", "0");
